import 'date-fns';
import React, {Component,Fragment} from "react";
import Alert from '../Alert'
import ReportElementComponent from './ReportElementComponent';
import ReportElementHeader from './ReportElementHeader';
import Chart from '../Chart.js';


class ReportElement extends Component{
    constructor(props){
        super(props);
        this.state = {
            open:"",
            msg:"",
            alertType:"",

        }
    }
      


    render(){
        return(
            <div>
                <ReportElementHeader></ReportElementHeader>
                <div>
                    <div style={{borderBottom:"1px solid black",marginTop:"1em"}}>
                        <div style={{marginLeft:"1em"}}>
                            <h4>Joined Users</h4>
                        </div>
                    </div>
                    <ReportElementComponent></ReportElementComponent>
                </div>
                <div>
                    <div style={{borderBottom:"1px solid black",marginTop:"1em"}}>
                        <div style={{marginLeft:"1em"}}>
                            <h4>Graph</h4>
                        </div>
                        <div style={{width:"60%",margin:'2px auto'}}>
                            <Chart ></Chart>
                        </div>
                    </div>
                </div>
                <div>
                    <Alert alertType={this.state.alertType} msg={this.state.msg} open={this.state.open} alertClose={()=>this.setState({open:false})}></Alert>
                </div>
            </div>
        )
    }

}

export default ReportElement;
