import MessageComponent from './MessageComponent';



class Ask extends Component{

    state={
        chats:[],
        postQuestion:""
    }

    createQuestionComponents(chat){
        console.log(chat)
        let comp;
        comp = (chat.answer==='')?(<div><MessageComponent type="answer" question={chat.answer}></MessageComponent></div>):(<div></div>)
        return(
            <div>
                <div>
                    <MessageComponent type="question" question={chat.question}></MessageComponent>
                </div>
                {comp}
             </div>
        )
    }
    render(){
        return(
            <div style={{height:'700px',width:'100%'}}>

                <div style={{margin:'0 auto',width:'30%'}}>

                </div>

                <div>

                </div>
            </div>
        )
    }
}

export default Ask;
