import 'date-fns';
import React, {Component,Fragment} from "react";
import TextField from '@material-ui/core/TextField';
import history from '../../history';
import DateSection from '../DateSection';
import Button from '@material-ui/core/Button';
import Pagination from '@material-ui/lab/Pagination';
import Chart from '../Chart';
import QuestionPageHeader from './QuestionPageHeader'
import axios from 'axios';
import QuestionComponent from './QuestionComponent';

const uri = "http://localhost:8080/reports";

let number = 0;

function getEventArrays( arr ){
    let newArr = [];
    for( let i = 0; i < arr.length; i++ ){
        for( let j = 0; j < arr[i].participants.length; j++ ){
            for( let k = 0; k < arr[i].participants[j].chats; k++ ){
                newArr.push(arr[i]);
            }
        }
    }
    return newArr;
}

function getParticipantArrays( arr ){
    let newArr = [];
    for( let i = 0; i < arr.length; i++ ){
        for( let j = 0; j < arr[i].participants.length; j++ ){
            for( let k = 0; k < arr[i].participants[j].chats; k++ ){
                newArr.push(arr[i].participants[j]);
            }
        }
    }
    return newArr;
}

function getChatsArrays( arr ){
    let newArr = [];
    for( let i = 0; i < arr.length; i++ ){
        for( let j = 0; j < arr[i].participants.length; j++ ){
            for( let k = 0; k < arr[i].participants[j].chats; k++ ){
                newArr.push(arr[i].participants[j].chats[k]);
            }
        }
    }
    return newArr;
}

function createQuestionComponents(event){
    number = number + 1;
    return(
        <QuestionComponent>
            number = {number}
        
            eventName={event.name} 
            time={event.startDate.substring(8,10) + "-"+ event.startDate.substring(5,7) + "-"+ event.startDate.substring(0,4) + "/" + event.startTime.substring(0,5)
                +"  -  " + event.endDate.substring(8,10) + "-"+ event.endDate.substring(5,7) + "-"+ event.endDate.substring(0,4) + "/" + event.endTime.substring(0,5)} 
            name={event.participants.n}
            tc={}
        </QuestionComponent>
    )
}

class QuestionPage extends Component{
    state={
        events:""

    }
    componentDidMount(){
        number = 0;
        axios.get(uri).then(response => {
            this.setState({allEvents:response.data,events:response.data.slice(0,10)})
            console.log(this.state.allEvents)
        });
    }

    handlePageChange(event,newPage){
        let arr = [...this.state.allEvents].slice(newPage*10 - 10 , newPage * 10);

        this.setState({page:newPage,events:arr});
    }
      


    render(){
        let pagination = (this.state.allEvents.length >= 10) ?<Pagination count={Math.ceil(this.state.allEvents.length/10)} page={this.state.page} color="primary" onChange={(event,newPage)=>{this.handlePageChange(event,newPage)}} />
        : <div></div>
        return(
            <div>
                <QuestionPageHeader></QuestionPageHeader>
                <div>
                    {this.state.events.map( event => createQuestionComponents(event) )}
                </div>
                <div style={{float:'right',margin:'2%'}}>
                    {pagination}
                </div>
                <div>
                </div>
            </div>
        )
    }

}

export default QuestionPage;
