import React from 'react'
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
 
const containerStyle = {
  width: '400px',
  height: '400px'
};
 
const center = {
  lat: 38,
  lng: 35
};
 
function MyComponent({locationChoosed},props) {
  const [map, setMap] = React.useState(null)
  const [newLat,setNewLat] = React.useState(null);
  const [newLng,setNewLng] = React.useState(null);
 
  const onLoad = React.useCallback(function callback(map) {
    const bounds = new window.google.maps.LatLngBounds();
    map.fitBounds(bounds);
    setMap(map)
  }, [])
 
  const onUnmount = React.useCallback(function callback(map) {
    setMap(null)
  }, [])

  return (
    <LoadScript
      googleMapsApiKey="AIzaSyBCeAgR3VSJLN36d8Ip2OGE2X64_K-7l2E"
    >
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
        onClick={(event)=>{
          if( props.enableOnClick == 'true'){
            locationChoosed({lat: newLat, lng: newLng});
            setNewLat(event.latLng.lat());
            setNewLng(event.latLng.lng());
          }
        }}
      >
        { /* Child components, such as markers, info windows, etc. */ }
        <Marker position={{lat: newLat, lng:newLng}}/>
      </GoogleMap>
    </LoadScript>
  )
}
 
export default React.memo(MyComponent)
