import React, { useState, useContext } from 'react';
import TextField from '@material-ui/core/TextField';

function Login(){
    const [username,setUsername] = React.useState("");
    const [password,setPassword] = React.useState("");
    const [open,setOpen] = React.useState(false);
    const {user,setUser} = useContext(UserContext);

    let alert;

    const login = async (a,b) => {
        return{
            username: a,
            password: b
        }
    }

        const handleLogin = async () =>{
        if(username == ""){
            alert = <Alert alertType="error" msg="Username cannot be empty" open={open} alertClose={()=>setOpen(false)}></Alert>
        }
        else if(password == "" ){
            alert = <Alert alertType="error" msg="Password cannot be empty" open={open} alertClose={()=>setOpen(false)}></Alert>
        }
        else{
           const user = await login(username,password);
           setUser(user);
        }

    }
    return(
        <div style={{height:"550px"}}>
            <div style={{width:"20%",justifyContent:"center", display:"flex",flexDirection:"column",marginTop:"10%",marginLeft:"500px"}}>
                <h1>Login</h1>
                <TextField
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    placeholder="Username"
                    value={username}
                    size="medium"
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    onChange={()=>setUsername(value)}
                />
                <TextField
                    id="standard-full-width2"
                    style={{ margin: 8 }}
                    placeholder="Password"
                    size="medium"
                    value={password}
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    onChange={()=>setPassword(value)}
                />
                <Button variant="contained" color="primary" onClick={handleLogin}>
                    Primary
                </Button>
            </div>
            <div>
                <Alert alertType={this.state.alertType} msg={this.state.msg} open={this.state.open} alertClose={()=>this.setState({open:false})}></Alert>
            </div>
        </div>
    )

}

export default Login;
