import 'date-fns';
import React, {Component} from "react";
import TextField from '@material-ui/core/TextField';
import SwitchSection from '../SwitchSection';
import history from '../../history';
import DateSection from './../DateSection';
import Alert from '../Alert'
import Button from '@material-ui/core/Button';
import MapContainer from '../../MapContainer';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import EditEventHeader from './EditEventHeader';
import axios from 'axios';
import { da } from 'date-fns/locale';

const uri = "http://localhost:8080/events"

function getMonthNameFromNumber(monthNumber){
    switch(monthNumber){
        case '01':
            return 'January';
        case '02':
            return 'February';
        case '03':
            return 'March';
        case '04':
            return 'April';
        case '05':
            return 'May';
        case '06':
            return 'June';
        case '07':
            return 'July';
        case '08':
            return 'August';
        case '09':
            return 'September';
        case '10':
            return 'October';
        case '11':
            return 'November';
        case '12':
            return 'December';
        default:
            return "error";
    }
}

function getMonthNumberFromName(monthName){
    switch(monthName){
        case 'Jan':
            return '01';
        case 'Feb':
            return '02';
        case 'Mar':
            return '03';
        case 'Apr':
            return '04';
        case 'May':
            return '05';
        case 'Jun':
            return '06';
        case 'Jul':
            return '07';
        case 'Aug':
            return '08';
        case 'Sep':
            return '09';
        case 'Oct':
            return '10';
        case 'Nov':
            return '11';
        case 'Dec':
            return '12';
        default:
            return "error";
    }
}

function fixDateForPicker(date){
    let year = date.substring(0,4);
    let month = getMonthNameFromNumber(date.substring(5,7));
    let day = date.substring(8,10);
    let hour = date.substring(11,13);
    let minute = date.substring(14,16);
    
    return new Date('' + month + ' ' + day + ', ' + year + ' ' + hour + ':' + minute + ':00');
}

function fixDateForJSON(date){
    let year = date.substring(11,15);
    let month = getMonthNumberFromName(date.substring(4,7));
    let day = date.substring(8,10);
    
    return ''+year+'-'+month+'-'+day;
}
function fixTimeForJSON(date){
    let hour = date.substring(16,18);
    let minute = date.substring(19,21);
    
    return ''+hour+':'+minute;
}

class EditEvent extends Component{
    constructor(props){
        super(props);
        this.state = {
            eventName: "",
            eventCapacity: 0,

            open: false,
            msg: "",
            alertType: "",

            locationLat: null,
            locationLng: null,
            locationText: "",

            startDate: "",
            endDate: "",

            switchGender: false,
            switchAge: false
        }
    }
    
    componentDidMount(){
        axios.get( uri + window.location.href.substring(window.location.href.indexOf('events')+7)).then(response =>{
            this.setState({
                eventName:response.data.name,
                eventCapacity:response.data.max,
                startDate:(response.data.startDate+"T"+response.data.startTime.substring(0,5)),
                endDate:(response.data.endDate+"T"+response.data.endTime.substring(0,5)),
                locationLat:response.data.lat,
                locationLng:response.data.lng
            })
        });
    }
    handleCreate(event){
        if((this.state.eventName === "")){
            this.setState({ alertType: "error", msg:"Event name can not be empty",open:!this.state.open});
        }
        else if( (this.state.eventCapacity === 0)){
            this.setState({ alertType: "error", msg:"Event capacity can not be empty",open:!this.state.open});
        }
        else{
            //Alert the user and pass the data to backend
            //this.setState({ alertType: "succes", msg:"Event Created",open:!this.state.open});
            history.push('/');
        }
    }
    handleInput(event){
        const name = event.target.name;
        const value = event.target.value;

        this.setState({
            [name]: value
        });
    }


    render(){
        return(
            <div>
                <EditEventHeader></EditEventHeader>
                <div className = "mainContainer">
                    <form>
                        <div>
                            <h2>Event Name(required)</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <div style={{height:'100px', marginTop:'1em',marginLeft:'1em'}}>
                                <TextField name= "eventName" id="standard-basic" value={this.state.eventName} label="Event Name" onChange={(event)=>this.handleInput(event)}/>
                            </div>
                            <h2>Event Capacity(required)</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <div style={{height:'100px', marginTop:'1em',marginLeft:'1em'}}>
                                <TextField type="number" name= "eventCapacity" id="standard-basic" value={this.state.eventCapacity} label="Event Capacity" onChange={(event)=>this.handleInput(event)}/>
                            </div>
                        </div>
                        <div>
                            <h2>Event Destination</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <div style={{display:'flex',justifyContent:'space-evenly',alignItems:'center'}}>
                                <div style={{marginTop:'10px',marginBottom:'10px'}}>
                                    <MapContainer curLat={35} curLng={38} locationChoosed={location => this.setState({locationLat:location.lat,locationLng:location.lng})}></MapContainer>
                                </div>
                                <div>
                                    <p>Enter Adress Here:</p>
                                    <div>
                                        <TextareaAutosize aria-label="empty textarea" placeholder="Empty" value={this.state.locationText} 
                                        onChange={(text)=>{this.setState({locationText:text})}}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div>
                            <h2>Time</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <DateSection curStartDate= {new Date(fixDateForPicker('2000-12-12T13:00'))} curEndDate={new Date('August 19, 1975 23:15:31')} data ='a' dateChoosed={date => {
                                this.setState({startDate: date.substring(0,date.indexOf("G")), 
                                endDate: date.substring(date.indexOf("start"),date.substring(date.indexOf("start"),date.indexOf("end")).indexOf("G"))})}}>
                            </DateSection>
                        </div>
                        <div>
                            <h2>Optinal Choices</h2>
                            <p>This part is for optional questions for the outside user, choose the ones you want to ask</p>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <SwitchSection switchsChoosed={choice => 
                                this.setState({switchAge:choice.switchAge,
                                               switchGender:choice.switchGender
                                              })}>
                            </SwitchSection>
                        </div>
                        <div style={{float:'right',marginRight:'100px'}}>
                            <Button variant="contained" color="primary" onClick={(event) => this.handleCreate(event)}>
                                Edit Event
                            </Button>
                        </div>
                    </form>
                </div>
                <div></div>
                <div>
                    <Alert alertType={this.state.alertType} msg={this.state.msg} open={this.state.open} alertClose={()=>this.setState({open:false})}></Alert>
                </div>
            </div>
        )
    }

}

export default EditEvent;
