import 'date-fns';
import React, {Component,Fragment} from "react";
import TextField from '@material-ui/core/TextField';
import history from '../../history';
import DateSection from '../DateSection';
import Button from '@material-ui/core/Button';
import Pagination from '@material-ui/lab/Pagination';
import MapContainer from '../../MapContainer';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import axios from 'axios';
import SurveyHeader from './SurveyHeader'
import SurveyComponent from './SurveyComponent'
import SurveyElementComponent from './SurveyElementComponent'
import { isThisSecond } from 'date-fns';

const uri = "http://localhost:8080/surveyQuestions/";

function getParticipant(arr){
    console.log(arr);
    for( let i = 0; i < arr.length; i++ ){
        if( arr[i].tcKimlikNo === localStorage.getItem('tc')){
            return arr[i];
        }
    }
}

function getPostData( event ){
    let postData = [];
    for( let i = 0; i < getParticipant(event.participants).surveyQuestions.length; i++ ){
        const data ={
            'questionName': getParticipant(event.participants).surveyQuestions[i].questionName,
            'answer': this.state.answer[i],
            'tcKimlikNo':localStorage.getItem('tc')
        }
        postData.push(data);
    }
    return postData;
}

let number = 0;
function createEventComponents(event){
    number = number + 1;
    console.log(event);
    return(
        <SurveyElementComponent
            question={event.questionName} 
            number={number}
            handleChange={(text,i)=>{handleTextArea(text,i)}}
        > 
        </SurveyElementComponent>
    )    
}

function handleTextArea(text,i){
    let arr = [...this.state.answers];
    arr[i] = text;

    this.setState({answers:arr});
}



class SurveyElement extends Component{
    constructor(props){
        super(props);
        this.state = {
            event:{},
            answers:[],
            loaded:false
        }
    }
    componentDidMount(){
        axios.post(uri+window.location.href.substring(window.location.href.indexOf("/surveyQuestions") + 16 ),{'tcKimlikNo':localStorage.getItem('tc')}).then(response => {
            this.setState({event:response.data,loaded:true})
            console.log(response)
        });
    }
    
    handleSubmit(event){
        axios.put(uri+window.location.href.substring(window.location.href.indexOf("/surveyQuestions")) + 16, getPostData(this.state.event) ).then(response=>{
            if(response.data.message==='ERROR'){
                console.log(response);
            }
            else{
                history.push("/events")
            }
        })
    }      


    render(){
        console.log("asdas")
        number = 0;
        return(
            <div width="100%">
                <div style={{marginLeft:'40%'}}> <h1>{this.state.event.name} Survey </h1> </div>
                <div style={{backgroundColor: 'black',height:'1px'}}></div>
                <div style={{display:'flex',flexDirection:'column',justifyContent:'space-evenly',width:'60%',border:'3px solid black',borderRadius:'25px'}}>
                    {(this.state.loaded)?getParticipant(this.state.event.participants).surveyQuestions.map( surveyQuestion => createEventComponents(surveyQuestion) ):<div></div>}
                </div>
                <div>
                    <Button variant="contained" color="primary" onClick={(event) => {this.handleSubmit(event)}}>
                        Submit
                    </Button>
                </div>

            </div>
        )
    }

}

export default SurveyElement;
