import React, {Component, useContext} from 'react';
import SideMenuItem from "./SideMenuItem";
import styles from "./MenuStyle.css";
import history from "../../history"
import { UserContext } from '../../UserContext';

class SideMenu extends Component{
    state={"user": UserContext}
    render(){
        console.log(styles.menuStyle);
        return(
            <div className = "menuStyle">
                <div onClick={()=> {history.push('/')}}>
                    <SideMenuItem header = "Homepage" icon = "Homepage"></SideMenuItem>
                </div>
                <SideMenuItem header = "Reports"  icon = "Reports"></SideMenuItem>

                <div onClick={() => { (this.state.user) ? ( history.push('/login') ) : (this.setState({"user":null}))}}>
                    <SideMenuItem header = { (this.state.user) ? "Log In" : "Log Out" } icon = "LogIn"></SideMenuItem>
                </div>
            </div>
        )
    }
}


export default SideMenu;
