import 'date-fns';
import React, {Component,Fragment} from "react";
import TextField from '@material-ui/core/TextField';
import AddEventHeader from './AddEventHeader';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Switch from '@material-ui/core/Switch';
import styles from "./AddEventStyle.css";
import {
    MuiPickersUtilsProvider,
    KeyboardTimePicker,
    KeyboardDatePicker
  } from "@material-ui/pickers";
import DateSection from './DateSection';

class AddEvent extends Component{


    render(){
        return(
            <div>
                <AddEventHeader></AddEventHeader>
                <div className = "mainContainer">
                    <form>
                        <div>
                            <h2>Event Name(required)</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <div style={{height:'100px', marginTop:'1em',marginLeft:'1em'}}>
                                <TextField id="standard-basic" label="Event Name"/>
                            </div>
                            <h2>Event Capacity(required)</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <div style={{height:'100px', marginTop:'1em',marginLeft:'1em'}}>
                                <TextField id="standard-basic" label="Event Capacity"/>
                            </div>
                        </div>
                        <div>
                            <h2>Time(required)</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <DateSection></DateSection>
                        </div>
                        <div>
                            <h2>Optinal Choices</h2>
                            <p>This part is for optional questions for the outside user, choose the ones you want to ask</p>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <div style={{height:'100px', display:'flex',flexDirection:'row', flexWrap:'wrap'}}>
                                <div>
                                    <FormControlLabel
                                        control={
                                        <Switch
                                            checked={state.checkedB}
                                            onChange={handleChange}
                                            name="checkedB"
                                            color="primary"
                                        />
                                        }
                                            label="Primary"
                                    />
                                </div>
                                <div>
                                    
                                </div>
                                <div>
                                    
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
                <div></div>
            </div>
        )
    }

}

export default AddEvent;
