import React from 'react';
import styles from './MenuStyle.css';
import HomeIcon from '@material-ui/icons/Home';
import AssessmentIcon from '@material-ui/icons/Assessment';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';


function SideMenuItem(props){
    let cmp;
    let newStyle;
    if( props.icon === 'Homepage' ){
        cmp = <HomeIcon style={{ color: '#15026b' }}></HomeIcon>;
        newStyle = 
        {
            height:'50px',
            borderBottom: '1px solid #15026b',
            borderTop: '1px solid #15026b'
        }
    }
    else if( props.icon === 'Reports'){
        cmp = <AssessmentIcon style={{ color: '#15026b' }}></AssessmentIcon>;
        newStyle = 'b';
    }
    else if( props.icon == 'LogIn'){
        cmp = <ExitToAppIcon style={{ color: '#15026b' }}></ExitToAppIcon>;
        newStyle ='a';
    }
    return(
        <div className= {newStyle}>
        <div className = 'menuItemStyle'>
            <div>
                {cmp}
            </div>
            <div>
                <text>{props.header}</text> 
            </div>
        </div>
        </div>
    )
}

export default SideMenuItem;
