import 'date-fns';
import React from 'react';
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import Snackbar from "@material-ui/core/Snackbar";
import MuiAlert from "@material-ui/lab/Alert";
import { makeStyles } from "@material-ui/core/styles";
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';
import { id } from 'date-fns/locale';
import SnackbarAlert from '../SnackbarAlert';

function DateSection(props) {
    const [selectedStartDate, setSelectedStartDate] = React.useState(new Date());
    const [selectedEndDate, setSelectedEndDate] = React.useState(new Date());



    const useStyles = makeStyles((theme) => ({
      root: {
        width: "100%",
        "& > * + *": {
          marginTop: theme.spacing(2)
        }
      }
    }));

    const classes = useStyles();
    const [open, setOpen] = React.useState(props.alert);
    const handleClose = (event, reason) => {
      if (reason === "clickaway") {
        return;
      }
  
      setOpen(false);
    };






  const handleStartDateChange = (date) => {
    if( selectedEndDate.getTime() >= date.getTime()){
      setSelectedStartDate(date);
    }
    else{
      setOpen(true)
    }
  };
  const handleEndDateChange = (date) => {
    if( date.getTime() >= selectedStartDate.getTime() ){
      setSelectedEndDate(date);
    }
    else{
      setOpen(true)
    }
  };

  return (
    <div>
      <div style = {{display:"flex",flexDirection:"row",justifyContent:"space-evenly",width: '75%'}}>
      <MuiPickersUtilsProvider utils={DateFnsUtils}>
          <KeyboardDatePicker
            disableToolbar
            variant="inline"
            format="dd/MM/yyyy"
            margin="normal"
            id="startDate"
            label="Start Date"
            value={selectedStartDate}
            onChange={handleStartDateChange}
            KeyboardButtonProps={{
              'aria-label': 'change date',
            }}
          />
          <KeyboardDatePicker
            disableToolbar
            variant="inline"
            format="dd/MM/yyyy"
            margin="normal"
            id="endDate"
            label="End Date"
            value={selectedEndDate}
            onChange={handleEndDateChange}
            KeyboardButtonProps={{
              'aria-label': 'change date',
            }}
          />
      </MuiPickersUtilsProvider>
      </div>
      <div style = {{display:"flex",flexDirection:"row",justifyContent:"space-evenly",width: '75%'}}>
        <MuiPickersUtilsProvider utils={DateFnsUtils}>
          <KeyboardTimePicker
              margin="normal"
              id="time-picker"
              label="Start Time"
              value={selectedStartDate}
              onChange={handleStartDateChange}
              KeyboardButtonProps={{
                'aria-label': 'change time',
              }}
            />
          <KeyboardTimePicker
              margin="normal"
              id="time-picker"
              label="End Time"
              value={selectedEndDate}
              onChange={handleEndDateChange}
              KeyboardButtonProps={{
                'aria-label': 'change time',
              }}
            />
        </MuiPickersUtilsProvider>
      </div>

      <div className={classes.root}>
      <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
        <SnackbarAlert onClose={handleClose} severity="error">
          This is a success message!
        </SnackbarAlert>
      </Snackbar>
    </div>


    </div>
  );
}

export default DateSection;
