import React, {Component} from "react";
import HomepageHeader from './HomepageHeader';
import EventComponent from './EventComponent';
import axios from 'axios';
import { UserContext } from "../../UserContext";
const uri = "http://localhost:8080/events/"

function createEventComponents(event){
    return (<EventComponent 
        header={event.name} 
        currentPeople={event.current} 
        maxPeople={event.max} 
        lat={event.lat}
        lng={event.lng}
        startTime={event.startDate + " / " + event.startTime}
        endTime={event.endDate + " / " + (event.endTime)}
    ></EventComponent>);
}
class Main extends Component{
    state={
        user:UserContext,
        page:0,
        change:0,
        events:[]
    }
    componentDidMount(){
        document.addEventListener('scroll', this.trackScrolling);
        axios.get(uri+ "" + this.state.page).then(response =>{
            this.setState({events:response.data})
            console.log(this.state.events)
        })
    }

    isBottom(scroller) {
        return scroller.getBoundingClientRect().bottom <= window.innerHeight;
    }
      
    componentWillUnmount() {
        document.removeEventListener('scroll', this.trackScrolling);
    }
    ac(){
        document.addEventListener('scroll', this.trackScrolling);
    }
    trackScrolling = () => {
            const wrappedElement = document.getElementById('scrollA');
            if (this.isBottom(wrappedElement)) {
                document.removeEventListener('scroll', this.trackScrolling);
                const arr = [...(this.state.events)];
                axios.get(uri + "" + (this.state.page + 1 )).then(response=>{
                    arr.push(response.data)
                    console.log(arr);
                    this.setState(prevState=> {
                        return{
                            page: prevState.page + 1
                        }
                    })
                })
        
        }
        setTimeout(function(){ },3000);
    };

    render(){
        return(
            <div id='scrollA' onScroll={this.handleScroll}>
                <HomepageHeader></HomepageHeader>
                <div style={{backgroundColor: 'black',height:'1px'}}></div>
                <div style={{height:'618px'}} >
                    {this.state.events.map( event => createEventComponents(event) )}
                </div>
            </div>
        )
    }
}


export default Main;
