import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';

function Login(){
    const [username,setUsername] = React.useState("");
    const [password,setPassword] = React.useState("");
    const [open,setOpen] = React.useState(false);

    let alert;

    const handleLogin = () =>{
        if(username == ""){
            alert = <Alert alertType={this.state.alertType} msg={this.state.msg} open={this.state.open} alertClose={()=>setOpen(false)}></Alert>
        }
    }
    return(
        <div style={{height:"550px"}}>
            <div style={{width:"20%",justifyContent:"center", display:"flex",flexDirection:"column",marginTop:"10%",marginLeft:"500px"}}>
                <h1>Login</h1>
                <TextField
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    placeholder="Username"
                    size="medium"
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />
                <TextField
                    id="standard-full-width2"
                    style={{ margin: 8 }}
                    placeholder="Password"
                    size="medium"
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                />
                <Button variant="contained" color="primary" onClick={handleLogin}>
                    Primary
                </Button>
            </div>
            <div>
                <Alert alertType={this.state.alertType} msg={this.state.msg} open={this.state.open} alertClose={()=>this.setState({open:false})}></Alert>
            </div>
        </div>
    )

}

export default Login;
