import React, { Component } from 'react';
import {formatRelative} from 'date-fns';
import { GoogleMap, InfoWindow, useLoadScript, Marker } from '@react-google-maps/api';
import { makeMaskFromFormat } from '@material-ui/pickers/_helpers/text-field-helper';
import MarkerClusterer from 'react-google-maps/lib/components/addons/MarkerClusterer';

const mapContainerStyle ={
    width: '100%',
    height: '100%'
}

const center = {
    lng: 72,
    lat: 72
}

export default function MapContainer() {
    const {isLoaded, loadError} = useLoadScript({
        googleMapsApiKey: 'AIzaSyBCeAgR3VSJLN36d8Ip2OGE2X64_K-7l2E'
    });
    const [markers,setMarkers] = React.useState([]);
    if(loadError) return "Error Loading";
    if(!loadError) return "Loading"
    return (
        <div>
            <GoogleMap 
                mapContainerStyle={mapContainerStyle}
                zoom={8}
                center={center}
                onClick={(event)=> {
                    console.log(1231239);
                    setMarkers((current) => [
                        ...current,
                        {
                            lat: event.latLng.lat(),
                            lng: event.latLng.lng(),
                            time: new Date()
                        },
                    ]);
                }}>
                {markers.map(marker => <Marker key = {marker.time.toISOString()}    position= {{lat:marker.lat , lng: marker.lng}}/>)}
            </GoogleMap>
        </div>
    );
  
}
