import axios from 'axios';

const uri = "http://localhost:8080/events/qrcode";

class QrCode extends Component{

    state={
        qrCode: '',
        loaded: false
    }


    componentDidMount(){
        console.log("edasdfasdfsdfasdfa");
        const postData ={
            'name':this.props.name,
            'tcKimlikNo':this.props.tc,
        };
        axios.post(uri, {
            method:"POST",
            headers:{ "Content-Type": "image/png",'Authorization': 'Bearer ' + window.localStorage["Access_Token"]}
        }).then(response=>{
            console.log(response);
            this.setState({
                qrCode: response.data
            });
        })
    }

    render(){

        return(
            <div>

                <div> {this.state.qrCode} </div>


            </div>

        );

    }


}

export default QrCode;
