import 'date-fns';
import React, {Component,Fragment} from "react";
import TextField from '@material-ui/core/TextField';
import AddEventHeader from './AddEventHeader';
import SwitchSection from './SwitchSection';
import history from './history';
import styles from "./AddEventStyle.css";
import DateSection from './DateSection';
import Snackbar from "@material-ui/core/Snackbar";
import MuiAlert from "@material-ui/lab/Alert";
import { makeStyles } from "@material-ui/core/styles";
import Alert from '../Alert'
import Button from '@material-ui/core/Button';
import MapContainer from '../../MapContainer';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';



class AddEvent extends Component{
    constructor(props){
        super(props);
        this.state = {
            eventName: "",
            eventCapacity: "",
            open: false,
            msg: "",
            alertType: "",
            locationLat: null,
            locationLng: null,
            locationText: "",
            switchGender: false,
            switchAge: false
        }
    }
      
    handleCreate(event){
        console.log(this.state.locationLat);
        if((this.state.eventName === "")){
            this.setState({ alertType: "error", msg:"Event name can not be empty",open:!this.state.open});
        }
        else if( (this.state.eventCapacity === "")){
            this.setState({ alertType: "error", msg:"Event capacity can not be empty",open:!this.state.open});
        }
        else if( this.state.locationLat == null || this.state.locationLng == null ){
            this.setState({ alertType: "error", msg:"You must select a location for the event",open:!this.state.open});
        }
        //history.push('/');
    }
    handleInput(event){
        const name = event.target.name;
        if((event.target.value.charAt(event.target.value.length() - 1) <= '9') && ( event.target.value.charAt(event.target.value.length() - 1) >= '0') ){
            let value = event.target.value;
        }
        this.setState({
            [name]: value
        });
    }


    render(){
        return(
            <div>
                <AddEventHeader></AddEventHeader>
                <div className = "mainContainer">
                    <form>
                        <div>
                            <h2>Event Name(required)</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <div style={{height:'100px', marginTop:'1em',marginLeft:'1em'}}>
                                <TextField name= "eventName" id="standard-basic" value={this.state.eventName} label="Event Name" onChange={(event)=>this.handleInput(event)}/>
                            </div>
                            <h2>Event Capacity(required)</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <div style={{height:'100px', marginTop:'1em',marginLeft:'1em'}}>
                                <TextField name= "eventCapacity" id="standard-basic" value={this.state.eventCapacity} label="Event Capacity" onChange={(event)=>this.handleInput(event)}/>
                            </div>
                        </div>
                        <div>
                            <h2>Event Destination(required)</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <div style={{display:'flex',justifyContent:'space-evenly',alignItems:'center'}}>
                                <div style={{marginTop:'10px',marginBottom:'10px'}}>
                                    <MapContainer locationChoosed={location => this.setState({locationLat:location.lat,locationLng:location.lng})}></MapContainer>
                                </div>
                                <div>
                                    <p>Enter Adress Here:</p>
                                    <div>
                                        <TextareaAutosize aria-label="empty textarea" placeholder="Empty" value={this.state.locationText} 
                                        onChange={(text)=>{this.setState({locationText:text})}}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div>
                            <h2>Time(required)</h2>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <DateSection dateChoosed={date => console.log(date)}></DateSection>
                        </div>
                        <div>
                            <h2>Optinal Choices</h2>
                            <p>This part is for optional questions for the outside user, choose the ones you want to ask</p>
                            <div style={{height: '1px',backgroundColor: 'black',width:'75%'}}></div>
                            <SwitchSection switchsChoosed={choice => 
                                this.setState({switchAge:choice.switchAge,
                                               switchGender:choice.switchGender
                                              })}>
                            </SwitchSection>
                        </div>
                        <div style={{float:'right',marginRight:'100px'}}>
                            <Button variant="contained" color="primary" onClick={(event) => this.handleCreate(event)}>
                                Create Event
                            </Button>
                        </div>
                    </form>
                </div>
                <div></div>
                <div>
                    <Alert alertType={this.state.alertType} msg={this.state.msg} open={this.state.open} alertClose={()=>this.setState({open:false})}></Alert>
                </div>
            </div>
        )
    }

}

export default AddEvent;
