import React from 'react';

function ChangingButtons(props) {
    let curChoice;
    if( props.isManager === 'true'){
        curChoice = 
        <div style={{display: 'flex', flexDirection:'row', justifyContent:'space-around'}}>
            <div>
                <button id = 'viewButt' > View </button>
            </div>
            <div>
                <button id = 'editButt'> Edit </button>
            </div>
            <div>
                <button id = 'deleteButt' > Delete </button>
            </div>
        </div>
    }
    else{
        curChoice = 
        <div>
            <button id = 'viewButt' style={{width:'10em', marginLeft:'2em'}}> View </button>
        </div>
    }
  return (
    <div>
        {curChoice}
    </div>
  );
}

export default ChangingButtons;
