import 'date-fns';
import React, {Component,Fragment} from "react";
import TextField from '@material-ui/core/TextField';
import history from '../../history';
import DateSection from '../DateSection';
import Alert from '../Alert'
import Button from '@material-ui/core/Button';
import MapContainer from '../../MapContainer';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';

/**
 * Errors: 
 * 1-You need to click 2 times to set the location
 * 2-End date not gettin correctly (getting realy close to fix)
 */



class Report extends Component{
    constructor(props){
        super(props);
        this.state = {
            open:"",
            msg:"",
            alertType:"",

        }
    }
      


    render(){
        return(
            <div>
                <div>
                    <div style={{display:"flex",flexDirection:"row",borderRadius:"20px",width:"90%",marginTop:"3px auto",border:"2px solid black"}}>
                        <div>Event Name: asdf </div>
                        <div>Time: </div>
                        <div><Button variant="contained" color="primary" onClick={()=>history.push("reports/1")}>
                                Primary
                            </Button>
                        </div>
                    </div>
                </div>
                <div>
                    <Alert alertType={this.state.alertType} msg={this.state.msg} open={this.state.open} alertClose={()=>this.setState({open:false})}></Alert>
                </div>
            </div>
        )
    }

}

export default Report;
