import React, {Component} from "react";
import HomepageHeader from './HomepageHeader';
import EventComponent from './EventComponent';
import axios from 'axios';
import { UserContext } from "../../UserContext";
import Pagination from '@material-ui/lab/Pagination';
const uri = "http://localhost:8080/events/"

function createEventComponents(event){
    return (<EventComponent 
        header={event.name} 
        currentPeople={event.current} 
        maxPeople={event.max} 
        lat={event.lat}
        lng={event.lng}
        startTime={event.startDate + " / " + (event.startTime).substring(0,5)}
        endTime={event.endDate + " / " + (event.endTime).substring(0,5)}
    ></EventComponent>);
}

class Main extends Component{
    state={
        user:UserContext,
        page:0,
        events:[]
    }
    componentDidMount(){
        document.addEventListener('scroll', this.trackScrolling);
        axios.get(uri+ "" + this.state.page).then(response =>{
            this.setState({events:response.data})
            console.log(this.state.events)
        })
    }

    handlePageChange(event,newPage){
        this.setState({page:newPage});
        axios.get(uri+ "" + this.state.page).then(response =>{
            this.setState({events:response.data})
            console.log(this.state.events)
        })
    }

    render(){
        return(
            <div>
                <div id='scrollA'  onScroll={this.handleScroll}>
                    <HomepageHeader></HomepageHeader>
                    <div style={{backgroundColor: 'black',height:'1px'}}></div>
                    <div >
                        {this.state.events.map( event => createEventComponents(event) )}
                    </div>
                </div>
                <div>
                    <Pagination count={10} page={this.state.page} color="primary" onChange={(event,newPage)=>{this.handlePageChange(event,newPage)}} />
                </div>
            </div>   
        )
    }
}


export default Main;
