import React, { useState, useContext,useEffect } from 'react';
import TextField from '@material-ui/core/TextField';
import {UserContext} from "../UserContext";
import Button from '@material-ui/core/Button';
import Alert from './Alert';

function Login(){
    const [username,setUsername] = React.useState("");
    const [password,setPassword] = React.useState("");
    const [open,setOpen] = React.useState(false);
    const [alert,setAlert] = React.useState("");
    const [msg,setMsg] = React.useState("");
    const {user,setUser} = useContext(UserContext);
    useEffect(()=> {console.log(user)})

    const loin = async (a,b) => {
        return{
            "username": a,
            "password": b
        }
    }
    const a = ()=>{
        return "a";
    }

    const handleChange = (event) => {
        if(event.target.name == "username"){
            setUsername(event.target.value);
        }
        else{
            setPassword(event.target.value);
        }
    }

        const handleLogin =  () =>{
        if(username == ""){
            console.log(open);
            setMsg("Username cannot be empty");
            setOpen(true);
            console.log(open);
        }
        else if(password == "" ){
            console.log(open);
            setMsg("Password cannot be empty");
            setOpen(true);
            console.log(open);
        }
        else{
           const u = loin(username,password);
           setUser(u);
           console.log(u);
        }

    }
    return(
        <div style={{height:"550px"}}>
            <div style={{width:"20%",justifyContent:"center", display:"flex",flexDirection:"column",marginTop:"10%",marginLeft:"500px"}}>
                <h1>Login</h1>
                <TextField
                    name = "username"
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    placeholder="Username"
                    value={username}
                    size="medium"
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    onChange={(event)=>handleChange(event)}
                />
                <TextField
                    name="password"
                    id="standard-full-width2"
                    style={{ margin: 8 }}
                    placeholder="Password"
                    size="medium"
                    value={password}
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    onChange={(event)=>handleChange(event)}
                />
                <Button variant="contained" color="primary" onClick={handleLogin}>
                    Primary
                </Button>
            </div>
            <div>
                <Alert alertType="error" msg={msg} open={open} alertClose={()=>setOpen(false)}></Alert>
            </div>
        </div>
    )

}

export default Login;
