import React, { useContext } from 'react';
import history from '../../history'
import {UserContext} from '../../UserContext'

function ChangingButtons(props) {
    const {user} = useContext(UserContext);

    let curChoice;
    if( user != null){
        curChoice = 
        <div style={{display: 'flex', flexDirection:'row', justifyContent:'space-around', flexWrap:'wrap'}}>
            <div>
                <button id = 'editButt' onClick={()=>history.push('/events/'+props.header)}> Edit </button>
            </div>
            <div>
                <button id = 'deleteButt' > Delete </button>
            </div>
        </div>
    }
    else{
        curChoice = 
        <div>
            <button onClick={()=>history.push('/join-event')} id = 'ab' style={{width:'10em', marginLeft:'2em'}}> Join </button>
        </div>
    }
  return (
    <div>
        {curChoice}
    </div>
  );
}

export default ChangingButtons;
