import React, { useState,useMemo } from 'react';
import logo from './logo.svg';
import './App.css';
import SideMenu from './components/Menu/SideMenu';
import Main from './components/Main/Main';
import AddEvent from './components/AddEvent/AddEvent';
import Header from'./components/Header';
import history from './history';
import {Router, Route, Link} from 'react-router-dom';
import { BrowserRouter, Redirect } from 'react-router-dom/cjs/react-router-dom.min';
import ViewEvent from './components/ViewEvent/ViewEvent';
import EditEvent from './components/EditEvent/EditEvent';
import Login from './components/Login';
import { UserContext } from './UserContext';
import Report from './components/Report/Report';
import ReportElement from './components/Report/ReportElement';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import { set } from 'date-fns';

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

function App() {
  const [user,setUser] = useState(null);
  const [open,setOpen] = useState(false);
  const [msg,setMsg] = useState('');
  const [alertType,setSeverity] = useState('');

  const providerValue = useMemo( () => ({user,setUser}) , [user,setUser]);

  const handleClose = ()=>{
    setOpen(false);
  }

  return (

    <Router history = {history}>
      <div style={{backgroundColor: "#fafbfc"}}>
          <SideMenu isLogedIn={user} logOut={()=>setUser(null)}></SideMenu>
          <div id="header"style={{marginLeft:'14%'}}>
            <Header></Header>
            <UserContext.Provider value= {providerValue}>
              <Route exact path="/" render={()=>{return(<Redirect to="/events"/>)}}/>
              <Route exact path="/events" component={Main}/>
              <Route exact path = "/add-event" component={(user) == null ? Main : AddEvent}/>
              <Route path = "/events/join/:name" component={ViewEvent}/>
              <Route exact path = "/events/view/:name" component={(user) == null ? Main : EditEvent}/>
              <Route exact path = "/reports" component={(user) == null ? <Main/> : Report}/>
              <Route exact path = "/reports/:name" component={(user) == null ? Main : ReportElement}/>
              <Route path = "/login" component={(user) != null ? Main : Login}/>
            </UserContext.Provider>
          </div>
          <Snackbar open={open} autoHideDuration={3000} onClose={handleClose}>
            <Alert onClose={handleClose} severity={alertType}>
              {msg}
            </Alert>
          </Snackbar>
      </div>
    </Router>
  );
}

export default App;
