import React, { useContext } from 'react';
import history from '../../history'
import {UserContext} from '../../UserContext'
import axios from 'axios';
const uri = "http://localhost:8080/events"

function ChangingButtons(props) {
    const {user} = useContext(UserContext);

    let curChoice;
    if( user != null){
        curChoice = 
        <div style={{display: 'flex', flexDirection:'row', justifyContent:'space-around', flexWrap:'wrap'}}>
            <div>
                <button id = 'editButt' onClick={()=>{
                    axios.delete(uri + "/" + props.name).then(response=>{console.log(response)});
                    history.push('/events/view/'+props.name);}}> Edit </button>
            </div>
            <div>
                <button id = 'deleteButt' > Delete </button>
            </div>
        </div>
    }
    else{
        curChoice = 
        <div>
            <button onClick={()=>(props.max>=props.current)?history.push('/events/join/' + props.name):console.log()} id = 'ab' style={{width:'10em', marginLeft:'2em'}}> Join </button>
        </div>
    }
  return (
    <div>
        {curChoice}
    </div>
  );
}

export default ChangingButtons;
