import React from 'react';
import Button from '@material-ui/core/Button';
import history from '../../history';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import { datePickerDefaultProps } from '@material-ui/pickers/constants/prop-types';



function SurveyElementComponent(props){
    let header= props.number + ") " + props.question;

    return(
        <div style={{height:'400px',width:'60%',border:'1px solid black', margin:'10px auto'}}>
            <div>
                <h3> {header}</h3>
            </div>
            <div >
                <TextareaAutosize  placeholder="Empty" value={props.text} onChange={(text)=>{props.handleChange(text,props.number-1)}}/>
            </div>
        </div>
    );
}

export default SurveyElementComponent;
