import React, { Component } from 'react';
import { GoogleMap, withGoogleMap, withScriptjs, Marker } from 'react-google-maps';
import { makeMaskFromFormat } from '@material-ui/pickers/_helpers/text-field-helper';

function Map(){
    return(
        <GoogleMap
            defaultZoom={10}
            defaultCenter={{lat:45, lng: -75}}
        />
    )
}
const WrappedMap = withScriptjs(withGoogleMap(Map));

export default function MapContainer(props) {
    const [marker,setMarker] = React.useState({});
    return (
        <div style={{width:'500px',height:'500px'}}>
            <WrappedMap
                googleMapURL={"https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyBCeAgR3VSJLN36d8Ip2OGE2X64_K-7l2E"}
                loadingElement={<div style={{height:"100%"}}/>}
                containerElement={<div style={{height:"100%"}}/>}
                mapElement={<div style={{height:"100%"}}/>}
                onClick={(event)=> setMarker(
                    {
                        lat: event.latLng.lat(),
                        lng: event.latLng.lng()
                    }
                )}
                >
                    <Marker
                        position={{
                            lat: marker.lat,
                            lng: marker.lng
                        }}
                    />
            </WrappedMap>

        </div>
    );
  
}
