import React from 'react';
import Button from '@material-ui/core/Button';
import history from '../../history';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';



function SurveyElementComponent(props){
    let header= props.number + ") " + props.question;

    return(
        <div style={{height:'400px',width:'800%',border:'1px solid black'}}>
            <div>
                <h3> {header}</h3>
            </div>
            <div >
                <TextareaAutosize aria-label="empty textarea" placeholder="Empty" value="Enter here..." onChange={(text)=>{props.handleChange(text,props.number)}}/>
            </div>
        </div>
    );
}

export default SurveyElementComponent;
