import 'date-fns';
import React, {Component,Fragment} from "react";
import TextField from '@material-ui/core/TextField';
import history from '../../history';
import DateSection from '../DateSection';
import Alert from '../Alert'
import Button from '@material-ui/core/Button';
import MapContainer from '../../MapContainer';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import ReportHeader from './ReportHeader';
import ReportComponent from './ReportComponent';
import Chart from '../Chart';

/**
 * Errors: 
 * 1-You need to click 2 times to set the location
 * 2-End date not gettin correctly (getting realy close to fix)
 */



class Report extends Component{
    constructor(props){
        super(props);
        this.state = {
            events:[],
            allEvents:[],
            open:"",
            msg:"",
            alertType:"",

        }
    }
    createReportComponent(event){
        return(
            <ReportComponent 
                name={event.name} 
                time={event.startDate.substring(8,10) + "-"+ event.startDate.substring(5,7) + "-"+ event.startDate.substring(0,4) + " / " + event.startTime.substring(0,5)
                    + event.endDate.substring(8,10) + "-"+ event.endDate.substring(5,7) + "-"+ event.endDate.substring(0,4) + " / " + event.endTime.substring(0,5)} 
                capacity={event.current + "/" + event.max}>
            </ReportComponent>
        )
    }
      


    render(){
        return(
            <div>
                <ReportHeader></ReportHeader>
                <div style={{width:"40%",margin:'20px auto'}}>
                    <Chart></Chart>
                </div>
                <div>
                    <ReportComponent name ></ReportComponent>
                </div>
                <div>
                    <Alert alertType={this.state.alertType} msg={this.state.msg} open={this.state.open} alertClose={()=>this.setState({open:false})}></Alert>
                </div>
            </div>
        )
    }

}

export default Report;
