import React, { useState, useContext,useEffect, Component } from 'react';
import TextField from '@material-ui/core/TextField';
import {UserContext} from "../UserContext";
import Button from '@material-ui/core/Button';
import Alert from './Alert';

class Login extends Component{
    style={
        "username":"",
        "password":"",
        "open":false,
        "user":UserContext
    }

   // useEffect(()=> {console.log(user)})

    loin = async (a,b) => {
        return{
            "username": a,
            "password": b
        }
    }

    handleChange = (event) => {
        if(event.target.name == "username"){
            this.setState({"username":event.target.value});
        }
        else{
            this.setState({"password":event.target.value});
        }
    }

        handleLogin = async () =>{
        if(this.state.username == ""){
            alert = <Alert alertType="error" msg="Username cannot be empty" open={this.state.open} alertClose={()=>this.setStyle({"open":false})}></Alert>
        }
        else if(this.state.password == "" ){
            alert = <Alert alertType="error" msg="Password cannot be empty" open={this.state.open} alertClose={()=>this.setStyle({"open":false})}></Alert>
        }
        else{
           const u = await this.loin(username,password);
           this.setState({"user":u});
           console.log(u);
        }

    }
    render(){
    return(
        <div style={{height:"550px"}}>
            <div style={{width:"20%",justifyContent:"center", display:"flex",flexDirection:"column",marginTop:"10%",marginLeft:"500px"}}>
                <h1>Login</h1>
                <TextField
                    name = "username"
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    placeholder="Username"
                    value={this.state.username}
                    size="medium"
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    onChange={(event)=>this.handleChange(event)}
                />
                <TextField
                    name="password"
                    id="standard-full-width2"
                    style={{ margin: 8 }}
                    placeholder="Password"
                    size="medium"
                    value={this.state.password}
                    margin="normal"
                    InputLabelProps={{
                        shrink: true,
                    }}
                    onChange={(event)=>this.handleChange(event)}
                />
                <Button variant="contained" color="primary" onClick={this.handleLogin}>
                    Primary
                </Button>
            </div>
            <div>
                {alert}
            </div>
        </div>
    )
                }

}

export default Login;
